import React, { useState, useEffect } from "react";
import { ChakraProvider, Box, Flex, theme } from "@chakra-ui/react";
import { ColorModeSwitcher } from "./ColorModeSwitcher";
import HomeBlurb from "./HomeBlurb";
import CurrentlyListening from "./CurrentlyListing";

function Home() {
    const [data, setData] = useState({
        display_name: null,
        current_song: null,
        current_artist: null,
    });

    useEffect(() => {
        fetch("/home").then((response) =>
            response.json().then((data) => {
                setData({
                    display_name: data.display_name,
                    current_song: data.current_song,
                    current_artist: data.current_artist,
                });
            })
        );
    }, []);

    const { display_name, current_song, current_artist } = data;

    return (
        <ChakraProvider theme={theme}>
            <Box p={100} textAlign="center" fontSize="40px">
                Welcome to my Spotify App!
                <ColorModeSwitcher justifySelf="flex-end" />
                <Flex m={40} align="center" justify="center" direction="column">
                    <HomeBlurb name={display_name} />
                    <CurrentlyListening song={current_song} artist={current_artist} />
                    {current_song && (
                        <Box m={5} fontSize="25">
                            You are currently listening to {current_song} by {current_artist}
                        </Box>
                    )}
                </Flex>
            </Box>
        </ChakraProvider>
    );
}

export default Home;
