import { extendTheme } from "@chakra-ui/react";
import { mode } from "@chakra-ui/theme-tools";

const styles = {
    colors: {
        black: "000",
    },
    global: (props) => ({
        body: {
            color: mode("#fcd303", "#fc0373")(props),
            bg: mode("white", "gray.800")(props),
        },
    }),
};

const theme = extendTheme({
    styles,
});

export default theme;
