import React, { useState, useEffect } from 'react';
import { ChakraProvider, Box, Flex, theme } from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';

function Home() {
  const [data, setData] = useState({
    display_name: '',
    current_song: '',
    current_artist: '',
  });

  useEffect(() => {
    fetch('/home').then(response =>
      response.json().then(data => {
        setData({
          display_name: data.display_name,
          current_song: data.current_song,
          current_artist: data.current_artist,
        });
      })
    );
  }, []);

  setUserRequest({
    loading: false,
    user: data.results[0],
  });

  return (
    <ChakraProvider theme={theme}>
      <Box textAlign="center" fontSize="xl">
        Welcome to my Spotify App!
        <ColorModeSwitcher justifySelf="flex-end" />
        <Flex align="center" justify="center" direction="column">
          <Box> Hi {data.display_name}</Box>
          <Box>
            Currently listening to {data.current_song} by {data.current_artist}
          </Box>
        </Flex>
      </Box>
    </ChakraProvider>
  );
}

export default Home;
