import React from "react";
import {
    ChakraProvider,
    Box,
    theme,
    Heading,
    useColorMode,
    useColorModeValue,
} from "@chakra-ui/react";
import { ColorModeSwitcher } from "./ColorModeSwitcher";
import LoginButton from "./LoginButton";

function Home() {
    const color = useColorModeValue("red", "blue");
    const { toggleColorMode } = useColorMode();
    return (
        <ChakraProvider theme={theme}>
            <ColorModeSwitcher justifySelf="flex-end" />
            <Box p={100} textAlign="center">
                <Heading fontSize="80px" color={color}>
                    Visualize Your Playback
                </Heading>

                <Box fontSize="30px">
                    <h3>
                        See your listening trends from the past <br />
                        and discover new tracks for the future
                    </h3>
                </Box>
                <LoginButton />
            </Box>
        </ChakraProvider>
    );
}

export default Home;
