import React, { useEffect, useState } from 'react';
import { ChakraProvider, Box, Flex, theme } from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import LoginButton from './LoginButton';

function Home() {
  const [login, setLogin] = useState('Not logged in');

  useEffect(() => {
    fetch('/home').then(response =>
      response.json().then(data => {
        setLogin(data.message);
      })
    );
  }, []);

  return (
    <ChakraProvider theme={theme}>
      <Box p={100} textAlign="center" fontSize="40px">
        Please log in to see the magic
        <ColorModeSwitcher justifySelf="flex-end" />
        <Flex m={40} align="center" justify="center" direction="column">
          <Box fontSize="25">{login}</Box>
          <LoginButton></LoginButton>
        </Flex>
      </Box>
    </ChakraProvider>
  );
}

export default Home;
