import React from "react";
import { ChakraProvider, useColorMode, Box, Heading } from "@chakra-ui/react";
import { ColorModeSwitcher } from "./ColorModeSwitcher";
import LoginButton from "./LoginButton";
import theme from "./../themes/theme";

function Home() {
    const { colorMode } = useColorMode();
    return (
        <ChakraProvider theme={theme}>
            <ColorModeSwitcher justifySelf="flex-end" />
            <Box p={100} textAlign="center">
                <Heading fontWeight="600" color="test" fontSize="80px">
                    Visualize Your Playback
                </Heading>

                <Box>
                    <Heading
                        color={colorMode === "light" ? "black" : "white"}
                        fontWeight="300"
                        fontSize="30px"
                    >
                        See your listening trends from the past <br />
                        and discover new tracks for the future
                    </Heading>
                </Box>
                <LoginButton />
            </Box>
        </ChakraProvider>
    );
}

export default Home;
